import React, { Component } from "react";
import { IntlShape, injectIntl } from "react-intl";
import {
  Theme,
  withStyles,
  WithStyles,
  createStyles,
} from "@material-ui/core/styles";
import {
  AppBar,
  Avatar,
  IconButton,
  Toolbar,
  Tooltip,
} from "@material-ui/core";
import { deepOrange } from "@material-ui/core/colors";
import { Typography } from "@material-ui/core";

const styles = (theme: Theme) =>
  createStyles({
    root: {
      width: "100%",
    },
    appbar: {
      [theme.breakpoints.up("sm")]: {
        width: "100%",
      },
    },
    title: {
      display: "none",
      [theme.breakpoints.up("sm")]: {
        display: "block",
      },
    },
    eplAvatar: {
      color: theme.palette.getContrastText(deepOrange[500]),
      backgroundColor: deepOrange[500],
    },
  });

interface TaskbarProps extends WithStyles<typeof styles> {
  intl: IntlShape;
  onEPLIconClick
}

type TaskbarState = {
  check: boolean;
  onClick: boolean;
};

class Taskbar extends Component<TaskbarProps, TaskbarState> {
  state: TaskbarState;
  constructor(props: TaskbarProps) {
    super(props);
    this.state = {
      check: true,
      onClick: false,
    };
  }

  onEPLIconClick = () => {
    this.setState((prevState) => {
      prevState.onClick;
    });
  };

  render() {
    const { classes, intl } = this.props;
    const { check } = this.state;

    return (
      <AppBar position="static" className={classes.appbar}>
        <Toolbar>
          <IconButton onClick={this.onEPLIconClick} color="inherit">
            <Tooltip
              title={intl.formatMessage({ id: "epl.avatar.name" })}
              placement="bottom-start"
            >
              <Typography variant="h5" className={classes.title}>
                {intl.formatMessage({ id: "epl.avatar.name" })}
              </Typography>
            </Tooltip>
          </IconButton>
        </Toolbar>
      </AppBar>
    );
  }
}

export default injectIntl(withStyles(styles)(Taskbar));
