import React, { Component } from "react";
import {
  Theme,
  WithStyles,
  createStyles,
  withStyles,
} from "@material-ui/core/styles";

import Drawer from "@material-ui/core/Drawer";
import FeatureService from "../../services/features/FeatureService";
import NavigationList from "./NavigationList";

const drawerWidth = 260;

const styles = (theme: Theme) =>
  createStyles({
    drawerPaper: {
      position: "relative",
      whiteSpace: "nowrap",
      width: drawerWidth,
      zIndex: 1000,
      transition: theme.transitions.create("width", {
        easing: theme.transitions.easing.easeInOut,
        duration: theme.transitions.duration.shortest,
      }),
      overflowX: "hidden",
      overflowY: "hidden",
      "&:hover": {
        overflowY: "auto",
        width: drawerWidth,
      },
      "&::-webkit-scrollbar": {
        display: "none",
      },
    },
    drawerPaperClose: {
      overflowX: "hidden",
      width: theme.spacing(7) + 1,
    },
    list: {
      width: 260,
    },
  });

interface NavigationProps extends WithStyles<typeof styles> {
  intl: IntlShape;
  open: boolean;
}

class Navigation extends Component<NavigationProps> {
  getItems() {
    const { intl } = this.props;
    const items: Array<any> = [];
    if (FeatureService.isGlobalSearchEnabled()) {
      items.push({
        id: 1,
        name: "search",
        title: intl.formatMessage({ id: "navigation.item.search" }),
        link: "/search",
