          items: [
            {
              id: 1,
              name: "systems",
              title: intl.formatMessage({ id: "navigation.item.systems" }),
              link: "/metadata/systems",
              abilities: [
                {
                  abilityName: "metadata",
                  actionName: "read",
                },
              ],
            },
            {
              id: 2,
              name: "environments",
              title: intl.formatMessage({ id: "navigation.item.environments" }),
              link: "/metadata/environments",
              abilities: [
                {
                  abilityName: "metadata",
                  actionName: "read",
                },
              ],
            },
            {
              id: 3,
              name: "tables",
              title: intl.formatMessage({ id: "navigation.item.tables" }),
              link: "/metadata/tables",
              abilities: [
