          ],
        },
        {
          id: 5,
          name: "mappings",
          title: intl.formatMessage({ id: "navigation.item.mappings" }),
          link: "/mappings",
          abilities: [
            {
              abilityName: "mappings",
              actionName: "read",
            },
          ],
        },
        {
          id: 6,
          name: "businessterms",
          title: intl.formatMessage({ id: "navigation.item.businessterms" }),
          link: "/business-terms",
          abilities: [
            {
