              abilities: [
                {
                  abilityName: "customassets",
                  actionName: "read",
                },
              ],
            },
          ],
          abilities: [
            {
              abilityName: "customassets",
              actionName: "read",
            },
          ],
        },
        {
          id: 10,
          name: "referencedata",
          title: intl.formatMessage({ id: "navigation.item.referencedata" }),
          link: "/reference-data",
          abilities: [
            {
              abilityName: "referencedata",
              actionName: "read",
            },
          ],
        },
        {
          id: 11,
          name: "releases",
          title: intl.formatMessage({ id: "navigation.item.releases" }),
          link: "/releases",
          abilities: [
            {
              abilityName: "releases",
              actionName: "read",
            },
          ],
        },
        {
          id: 12,
          name: "requirements",
          title: intl.formatMessage({ id: "navigation.item.requirements" }),
          link: "/requirements",
          abilities: [
            {
              abilityName: "requirements",
              actionName: "read",
            },
          ],
        },
        {
          id: 13,
          name: "reports",
          title: intl.formatMessage({ id: "navigation.item.reports" }),
          link: "/reports",
          abilities: [
            {
              abilityName: "reports",
              actionName: "read",
            },
          ],
        },
      ]
    );
    return items;
  }

  render() {
    const { classes, open } = this.props;
    const items = this.getItems();
    const depth = 0;

    return (
      <Drawer
        variant="permanent"
        classes={{
          paper: `${classes.drawerPaper} ${
            open ? null : classes.drawerPaperClose
          }`,
        }}
        anchor="left"
        open={open}
      >
        <div className={classes.list} role="presentation">
          <NavigationList items={items} depth={depth} />
        </div>
      </Drawer>
    );
  }
}

export default injectIntl(withStyles(styles)(Navigation));
