import { IntlShape, injectIntl } from "react-intl";
import React, { Component } from "react";
import {
  Theme,
  WithStyles,
  createStyles,
  withStyles,
} from "@material-ui/core/styles";

import Drawer from "@material-ui/core/Drawer";
import NavigationList from "./NavigationList";

const drawerWidth = 260;

const styles = (theme: Theme) =>
  createStyles({
    drawerPaper: {
      position: "relative",
      whiteSpace: "nowrap",
      width: drawerWidth,
      zIndex: 1000,
      transition: theme.transitions.create("width", {
        easing: theme.transitions.easing.easeInOut,
        duration: theme.transitions.duration.shortest,
      }),
      overflowX: "hidden",
      overflowY: "hidden",
      "&:hover": {
        overflowY: "auto",
        width: drawerWidth,
      },
      "&::-webkit-scrollbar": {
        display: "none",
      },
    },
    drawerPaperClose: {
      overflowX: "hidden",
      width: theme.spacing(7) + 1,
    },
    list: {
      width: 260,
    },
  });

interface NavigationProps extends WithStyles<typeof styles> {
  intl: IntlShape;
  open: boolean;
}

class Navigation extends Component<NavigationProps> {
  getItems() {
    const { intl } = this.props;
    const items: Array<any> = [];

    items.push([
      {
        id: 1,
        name: "home",
        title: intl.formatMessage({ id: "navigation.item.home" }),
        link: "/home",
      },
      {
        id: 2,
        name: "players",
        title: intl.formatMessage({ id: "navigation.item.players" }),
        link: "/players",
      },
    ]);
    return items;
  }
  render() {
    const { classes, open } = this.props;
    const items = this.getItems();
    const depth = 0;
    return (
      <Drawer
        variant="permanent"
        classes={{
          paper: `${classes.drawerPaper} ${
            open ? null : classes.drawerPaperClose
          }`,
        }}
        anchor="left"
        open={open}
      >
        <div className={classes.list} role="presentation">
          <NavigationList items={items} depth={depth} />
        </div>
      </Drawer>
    );
  }
}

export default injectIntl(withStyles(styles)(Navigation));
